package baiscjava;

public class StringConcatArithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 81;
		int b = 9;
		System.out.println(a+b);
		
		String g = "Sunil";
		String h = " Kumar";
		System.out.println(g+ h);
		
		System.out.println(a+b+g+h);
		System.out.println(g+h+a+b);
		
		char c1 = 'a';
		char c2 = 'b';
		System.out.println(c1+c2+g);
		System.out.println(h+c1+c2);

	}

}
